import React,{useState} from 'react'
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import {ThumbUpAltIcon,ThumbUpAltOutlined} from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete'
import MoreHorizIcon from '@material-ui/icons/MoreHoriz'
import moment from 'moment';
import useStyles from './styles'
import {useDispatch} from 'react-redux';
import { deletePost,likePost} from '../../../actions/posts';
import { useAuth0 } from '@auth0/auth0-react';
const Post = ({ post,setCurrentId }) => {
  const [likes,setLikes]=useState(post?.likes);
  const classes = useStyles()
  const dispatch=useDispatch();
  const {user}=useAuth0();
  const userId = user?._id;
  const hasLikedPost = post.likes.find((like) => like === userId);
  const handleLike=async()=>{
    dispatch(likePost(post._id));
    if(hasLikedPost){
      setLikes(post.likes.filter((id)=>id!==userId));
    }else{
  setLikes([...post.likes, userId])
    }
  };

  const Likes = () => {
    if (likes.length > 0) {
      return likes.find((like) => like === userId) // checking user Id for one like per person
        ? (
          <><ThumbUpAltIcon fontSize="small" />&nbsp;{likes.length > 2 ? `You and ${likes.length - 1} others` : `${likes.length} like${likes.length > 1 ? 's' : ''}`}</>
        ) : (
          <><ThumbUpAltOutlined fontSize="small" />&nbsp;{likes.length} {likes.length === 1 ? 'Like' : 'Likes'}</>
        );
    }

    return <><ThumbUpAltOutlined fontSize="small" />&nbsp;Like</>;
  };
  return (
    <>
    <Card className={classes.card}>
      <CardMedia className={classes.media} image={post.selectedFile} title={post.title} />
      <div className={classes.overlay}>
        <Typography variant='h6'>{post.creator}</Typography>
        <Typography variant='body2'>{moment(post.createdAt).fromNow()}</Typography>
      </div>
      <div className={classes.overlay2}>
        <Button
          style={{ color: 'white' }}
          size="small"
          disabled={!user}
          onClick={() => {setCurrentId(post._id)}}>
          <MoreHorizIcon fontSize='medium' />
        </Button>
      </div>
      <div className={classes.details}>
        <Typography variant='body2' color='textSecondary'> {post.tags.map((tag) => `#${tag} `)}</Typography>
      </div>
      <Typography className={classes.title} variant='h5' gutterBottom> {post.title}</Typography>

      <CardContent>

        <Typography  variant='body2' color='textSecondary' component='p'> {post.message}</Typography>


      </CardContent>
      <CardActions className={classes.cardActions}>
        <Button size='small' color='primary'disabled={!user} onClick={()=>dispatch((likePost(post._id)))}>
         <Likes/>
        </Button>
        <Button size='small' color='primary' disabled={!user} onClick={()=>dispatch(deletePost(post._id))}>
          <DeleteIcon fontSize='small' />
          delete
        </Button>
      </CardActions>
    </Card>
  </>
  )
}

export default Post
